import React, { useState } from "react";
import { ReactComponent as Weather_icons_cloudy } from "../SVG/weather_icons_cloudy.svg";
import type { Pos } from "../interface/pos";
import apiFetcher from "../shared/apiFetcher";

const ActualPos = () => {
  const [location, setLocation] = useState<Pos>();
  const [userInput, setUserInput] = useState<string>();
  const [errorMessage, setErrorMessage] = useState("");
  const date = new Date();

  React.useEffect(() => {
    navigator.geolocation.getCurrentPosition((pos) => {
      let loc: Pos = {
        longitude: pos.coords.longitude.toString(),
        latitude: pos.coords.latitude.toString(),
      };
      setLocation(loc);
    });
  }, [location]);

  const handleSubmit = async () => {
    console.log(userInput);
    if (!userInput) {
      return;
    }
    try {
      await apiFetcher({
        url: `http://api.weatherapi.com/v1/current.json?key=${process.env.REACT_APP_APIKEYW}&q=${userInput}&aqi=no`,
      });
    } catch (error) {
      setErrorMessage("The chosen location is not available.");
    }
  };
  return (
    <>
      <div className="bg-white rounded-lg w-2/3 m-auto h-4/5 relative top-10 border-2 border-gray-200">
        {/*
        <Weather_icons_cloudy /> */}
        <div className="flex justify-center p-1">
          <p className="pr-2">Choose a zip code or an address </p>
          <input
            className="rounded-md bg-slate-100 border-2 border-black ml-3"
            placeholder="75018 / Paris"
            onChange={(e) => setUserInput(e.target.value)}
          />
          <button
            onClick={() => handleSubmit()}
            className="bg-cyan-200 rounded-md box-border text-black border-2 border-black cursor-pointer ml-2 overflow-hidden relative text-center"
          >
            {" "}
            Search{" "}
          </button>
          {errorMessage && <p className="text-red-600"> {errorMessage} </p>}
        </div>
      </div>
    </>
  );
};

export default ActualPos;
