import React, { useState } from "react";
import { ReactComponent as Weather_icons_cloudy } from "../SVG/weather_icons_cloudy.svg";
import type { Pos } from "../interface/pos";
import apiFetcher from "../shared/apiFetcher";

const ActualPos = () => {
  const [location, setLocation] = useState<Pos>();
  const date = new Date();

  React.useEffect(() => {
    navigator.geolocation.getCurrentPosition((pos) => {
      let loc: Pos = {
        longitude: pos.coords.longitude.toString(),
        latitude: pos.coords.latitude.toString(),
      };
      setLocation(loc);
    });
  }, [location]);
  return (
    <>
      <div className="bg-white rounded-lg w-1/2 m-auto h-4/5 relative top-10 border-2 border-gray-200">
        <p> {location?.latitude}</p>
        <p> {location?.longitude}</p>
        <p> {date.getFullYear()} </p>
        <button
          onClick={() =>
            console.log(
              apiFetcher({
                url: `http://api.weatherapi.com/v1/current.json?key=${process.env.REACT_APP_APIKEYW}&q=92069&aqi=no`,
              })
            )
          }
        >
          Click me
        </button>
        <Weather_icons_cloudy />
      </div>
    </>
  );
};

export default ActualPos;
